def parse(instructions):
    p1=0
    p2=None
    for idx, i in enumerate(instructions):
        if p1 == -1 and p2 is None:
            p2=idx
        if i == '(':
            p1 += 1
        else:
            p1 -= 1
    return [p1, p2]


